from langflow import CustomComponent
from langchain.schema import Document
import requests

class HTTPRequestComponent(CustomComponent):
    display_name = "HTTP Request"
    description = "This component sends HTTP requests and returns the response."

    def build_config(self) -> dict:
        methods = ["GET", "POST", "PUT", "DELETE"]
        return {
            "method": {
                "options": methods,
                "value": methods[0],
                "display_name": "HTTP Method"
            },
            "url": {
                "display_name": "URL"
            },
            "headers": {
                "display_name": "Headers",
                "field_type": "dict",
                "required": False,
                "multiline": True
            },
            "body": {
                "display_name": "Body",
                "field_type": "str",
                "required": False,
                "multiline": True
            }
        }

    def build(self, method: str, url: str, headers: dict = {"Accept": "*/*"}, body: str = None) -> Document:
        response = requests.request(method, url, headers=headers, data=body)
        self.repr_value = f"Response Status Code: {response.status_code}"
        return Document(page_content=response.text)
